Scripts.OnClick:
if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOn");
else
	PlaySound("igMainMenuOptionCheckBoxOff");
end
Mazzifier_HandleCheckbutton(this);
MazzleUI:MouseClickEffect()

Scripts.OnEnter:
if ( this.tooltipText ) then
	GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
	GameTooltip:SetText(this.tooltipText, nil, nil, nil, nil, 1);
end
if ( this.tooltipRequirement ) then
	GameTooltip:AddLine(this.tooltipRequirement, "", 1.0, 1.0, 1.0);
	GameTooltip:Show();
end

Scripts.OnMouseDown:
local parent = this:GetParent();
if (arg1 == "LeftButton") then
	parent.isMoving = true;
	parent:StartMoving();
end

Scripts.OnMouseUp:
local parent = this:GetParent();
if (parent.isMoving) then
	parent:StopMovingOrSizing();
	parent.isMoving = nil;
end

Scripts.OnHide:
local parent = this:GetParent();
if (parent.isMoving) then
	parent:StopMovingOrSizing();
	parent.isMoving = nil;
end

Scripts.OnLoad:
this:SetBackdropBorderColor(0.4, 0.4, 0.4);
this:SetBackdropColor(0.15, 0.15, 0.15);
getglobal(this:GetName().."Title"):SetText("What is the aspect ratio of your screen? (width/height)");

this:SetBackdropBorderColor(0.4, 0.4, 0.4);
this:SetBackdropColor(0.15, 0.15, 0.15);
getglobal(this:GetName().."Title"):SetText("Which portions of the UI would you like to Mazzify?");
getglobal(this:GetName().."Note"):SetText("|CFFFF0000Note:|CFFFFFFFF The above checkboxes determine whether that part of the UI is modified \nand whether the relevant options in the rest of the Mazzifier are disabled or enabled.");

this:SetBackdropBorderColor(0.4, 0.4, 0.4);
this:SetBackdropColor(0.15, 0.15, 0.15);
getglobal(this:GetName().."Title"):SetText("How large do you like your chat window fonts?");

getglobal(this:GetName().."Title"):SetText("Please answer the following questions to determine which add-ons the Mazzifier will enable:");
this:SetBackdropBorderColor(0.4, 0.4, 0.4);
this:SetBackdropColor(0.05, 0.05, 0.05);

							this:SetBackdropBorderColor(0.4, 0.4, 0.4);
							this:SetBackdropColor(0.15, 0.15, 0.15);
							getglobal(this:GetName().."Title"):SetText("The following add-ons will be configured:");
                            getglobal(this:GetName().."Note"):SetText(TEXT("|CFFFF0000Warning:|CFFFFFFFF Some checkboxes above may be grayed out because the corresponding\nadd-on is currently disabled.  Re-enable the add-on first if you want to mazzify it."));

this:SetBackdropBorderColor(0.4, 0.4, 0.4);
this:SetBackdropColor(0.15, 0.15, 0.15);
getglobal(this:GetName().."Title"):SetText("What button layout would you like to use?");
